INSERT INTO `companynews` VALUES ('290098f58c35b92f7b97c56084da2464c14139b6', '31320f22ce19d976c93fd38166b535049cdca337', '陕西理工学院与汉中庆瑞丰食药用菌科技有限公司签署战略合作协议 ', '公司走访', '2017-05-31 14:36:58', '<p>\n	4月3日上午，陕西理工学院与汉中庆瑞丰食药用菌科技有限公司战略合作协议签字仪式在庆瑞丰食药用菌科技有限公司举行。陕西理工学院科技处、生工学院、陕西省食药用菌工程技术中心、陕西省黑色有机食品工程技术中心负责人，洋县科技局及该公司领导出席了签约仪式。</p>\n<p>\n	汉中庆瑞丰食药用菌科技有限公司党支部书记张新明致欢迎辞，介绍了公司基本情况及发展规划。科技处副处长、学科建设党总支书记李志峰处长介绍了陕西理工学院科技工作现状，并强调围绕当地经济建设，积极为地方区域经济和社会发展搭桥服务，提供科技和智力支持是学校科技工作的重要任务和使命。庆瑞丰食药用菌科技有限公司总经理杨新庆汇报了公司的发展历史，对陕西理工学院多年来在支持公司专利申报、实验室和基地建设方面的科技服务表示了衷心感谢，同时提出了公司发展方向和面临的主要技术问题。洋县科技局副局长闫建洲对校企成功合作表示了祝贺，希望陕西理工学院借助人才、科技和研究平台优势，充分开发区域优势资源，在陕西理工学院与洋县人民政府合作框架战略大前提下，通过主动为企业开展科技服务，实现联合创新，共同为区域经济社会发展作出新贡献。</p>\n<p>\n	随后，汉中庆瑞丰食药用菌科技有限公司总经理杨新庆和陕西理工学院科技处李志峰书记分别代表甲乙双方在校企战略合作协议上签字。</p>\n<p>\n	仪式结束后，生工学院、陕西省食药用菌工程技术中心、陕西省黑色有机食品工程技术中心负责人与企业技术人员就相关专业技术问题开展了诚挚又热烈的交流，就具体的技术合作进行了深层次的的洽谈，确定了近期合作的重点项目。</p>\n<p>\n	与会人员同时考察了公司食药用菌实验平台、生产车间、大球盖菇及魔芋品种选育示范基地。当天下午，由科技处带队，对陕西理工学院合作企业洋县双亚有机农业集团有限公司、<strong>陕西朱鹮黑米酒业有限公司</strong>进行了走访，了解了科技合作落实情况。</p>\n', '公司走访', '', '', '', '1');
INSERT INTO `companynews` VALUES ('d846282755db16093016e0f122202bd0555bc350', 'f45b3b266e14195fefcbcab0bc66b396880a302e', '2011年1月22日洋县玉虎化工有限责任公司6万吨/年联醇简介', 'www.hzyhhg.com', '2017-05-31 14:37:03', '<p>\n	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2011年1月22日洋县玉虎化工有限责任公司6万吨/年联醇简介：6万吨/年（一期3万吨）联醇是洋县玉虎化工有限责任公司收购原洋县氮肥厂以后在调整发展战略中的重要举措之一，也是洋县重点建设项目之一。</p>\n', '6万吨/年联醇简介', '', '', '', '3');
INSERT INTO `companynews` VALUES ('f8e2948a580b7e3f5ab4524d8f2e254bcb23054f', 'f45b3b266e14195fefcbcab0bc66b396880a302e', '中外专家来我公司参观指导', 'www.hzyhhg.com', '2017-05-31 14:39:42', '<p>\n	&nbsp; &nbsp; &nbsp; &nbsp; 2012年8月17日上午，西北农林科技大学中国土壤植物营养学专家，中国氮素循环学术带头人李生秀教授、来航线教授、陈力成教授、王教授会同美国著名土壤学专家、美国土壤学会主席BA&bull;斯图威特教授和美国作物学博士布拉斯来玉虎化工益民绿色农业科技有限责任公司进行参观考察和指导。李生秀教授一行着重参观了朱鹮牌系列</p>\n', '中外专家来我公司参观指导', '', '', '', '0');
