INSERT INTO `companyproduct` VALUES ('be21ba1860ee1bc00db99acfd5571271f0d58823', '25ba7d18767019f4184b8baf8b7b718d9b4f5137', '双亚周大黑洋县黑米3kg礼盒装', '<p align=\"left\">\n	洋县黑米、红米种植明确历史已有2000多年。1980年，考古工作者在洋县溢水河流域的范坝村发掘的春秋平王姬宜臼年间古墓葬内发现黑米、红米的痕迹。</p>\n<p align=\"left\">\n	用红黑二米作贵族达官的陪葬品，表明黑米、红米种植早在2600年前。《全唐诗》第855卷005首记载&ldquo;一粒红稻饭，几滴牛颔血。珊瑚枝下人，衔杯吐不歇&rdquo;，说明红稻的突出特征颜色红，和农人如牛般辛劳。唐乾宁4年（897年），诗人韦庄被聘为西川书记，赴任途经洋州傥骆古道，写诗《稻田》（《全唐诗》第697卷062首）&ldquo;绿波春浪满前陂，极目连云罢亚肥。更被鹭鹚千点雪，破烟来入画屏飞&rdquo;，说明洋县绿稻种植的美丽画面。</p>\n', '50.00', null, '10.0', '0', 'upload/pic/2017053115080056.jpg', '2017-05-31 15:05:18');
