INSERT INTO `companyproduct` VALUES ('8865d9572cd6774201ea8702cd4d3a62b5fdd334', '25ba7d18767019f4184b8baf8b7b718d9b4f5137', '双亚有机五彩米500g袋装', '<p>\n	五彩米（黑,红，黄，绿，紫）产于世界珍禽朱鹮栖息地陕西汉中洋县，历来被视为米中珍品。</p>\n<p>\n	洋县黑米在洋县具有三千多年的种植历史，是地理标志保护产品，自古有&ldquo;月米&rdquo;，&ldquo;长寿米&rdquo;&ldquo;米中珍品&rdquo;等美称。常食洋县黑米，具有滋阴补肾，益气强身，明目活血的作用。</p>\n<p>\n	&nbsp;洋县红米俗称&ldquo;胭脂米&rdquo;，以其色红，气香，味醇称奇于世，有&ldquo;清热润肺&rdquo;宁心爽神，滋补肾肝&rdquo;。</p>\n<p>\n	&nbsp;洋县紫香米，表皮有一薄层紫色物质，紫米煮饭，味道极香，口感极好，民间作为补品，有紫糯米或者&ldquo;药谷&rdquo;之称。</p>\n<p>\n	&nbsp;洋县黄米富含蛋白质，碳水化合物，微生素B，维生素E，锌，铜，锰等营养元素，具有益阴，利肺，利肠之功效。</p>\n', '100.00', null, '10.0', '0', 'upload/pic/2017053115070495.jpg', '2017-05-31 15:05:14');
