INSERT INTO `companyproduct` VALUES ('16352d28954365b65f295e21a1bbc2836dc31ecd', 'e3e3e5d3ffa64bf51882fd19d5287b3da15c2844', '5000克香丝米', '<p>\n	<span style=\"color: rgb(0, 0, 0); font-family: 宋体; line-height: 24px;\">&nbsp; &nbsp;&ldquo; 晶霞牌&rdquo;香丝米产于世界珍禽朱鹮栖息地&mdash;洋县。地处秦岭以南，与长江以南地区相比昼夜温差大、生育期长的特点，其生产的有机籼米营养成份高、适口性强、品质优。</span><br style=\"color: rgb(0, 0, 0); font-family: 宋体; line-height: 24px;\" />\n	<span style=\"color: rgb(0, 0, 0); font-family: 宋体; line-height: 24px;\">&nbsp; &nbsp; 本品在种植、加工过程中依照有机生产技术规程不使用化肥、农药、生长激素、化学添加剂、化学色素和防腐剂等化学物质，是一种真正源于自然、高营养、高品质的有机食品。</span></p>\n', '150.00', null, '1.0', '0', 'upload/pic/2017053115055577.jpg', '2017-05-31 15:05:59');
