INSERT INTO `companyproduct` VALUES ('b5594bba6e548fe7cf056cb5cf6979b86aeea4f1', '25ba7d18767019f4184b8baf8b7b718d9b4f5137', '洋县有机红大米糙米1000g', '<p>\n	汉中特产红香米俗称&ldquo;胭脂米&rdquo;，以其色红，气香，味醇称奇于世。香米呈白色透明状，形状稍扁，做米饭浓香四溢，久放不馊。为历代陕西供奉朝廷贡品，有&ldquo;清热润肺，宁心爽神，滋补肾肝&rdquo;之功效。由两千多年前西汉外交家，&ldquo;丝绸之路&rdquo;开拓者张骞首先发现，并在他的家乡城固县和洋县进行种植。香米的生长对水土的要求十分严格，所以洋县只有个别村庄种植，亩产只有100公斤上下，所以格外珍贵。</p>\n', '28.00', null, '10.0', '0', 'upload/pic/2017053115035468.jpg', '2017-05-31 15:05:11');
