INSERT INTO `companyproduct` VALUES ('82d5ffbb10a855b9ffbc095dc45569eef33d31b5', '31320f22ce19d976c93fd38166b535049cdca337', '西安朱鹮黑米酒', '<p>\n	<span style=\"color:#000000;\"><span style=\"font-family: \'Hiragino Sans GB\', Tahoma, Arial, 宋体, sans-serif; line-height: 18px;\"><span style=\"background-color:#f0ffff;\">黑米酒，是一种以黑米为原料制作的酒。将黑糯米进行脱糠皮处理，从糠皮中提取黑色素液，同时，对已脱掉糠皮的白色精黑米经发酵酿制过程，制出黄酒，在黄酒中加入适量的黑色素液，就制出黑米酒。</span></span></span></p>\n', '138.00', null, '10.0', '0', 'upload/pic/2017053115180229.png', '2017-05-31 15:05:06');
