INSERT INTO `companyrecruit` VALUES ('988a392d015fda1155aa71ddbcee2ee54af6f1c4', '25ba7d18767019f4184b8baf8b7b718d9b4f5137', '营销部经理', '<p>\n	<span font-size:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\">&nbsp;1、地区经销商</span><br line-height:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\" />\n	<span font-size:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\">&nbsp;&nbsp;（1）市场保证金；</span><br line-height:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\" />\n	<span font-size:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\">&nbsp;&nbsp;（2）首批货款；</span><br line-height:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\" />\n	<span font-size:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\">&nbsp;&nbsp;（3）店面要求：经营店面要求50㎡以上，店面按照公司统一标准装修；</span><br line-height:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\" />\n	<span font-size:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\">&nbsp;&nbsp;（4）两年内开设不少于2家专卖店；</span><br line-height:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\" />\n	<span font-size:=\"\" microsoft=\"\" style=\"color: rgb(0, 0, 0); font-family: \" widows:=\"\">&nbsp;&nbsp;（5）逐步建立加盟地区产品渠道销售系统。</span></p>\n', '17719714909', '400-666-5116', '32134@qq.com', '王斌', null, '2017-05-20 06:22:56', '陕西省汉中市洋县有机产业园区（洋县南环路东端）', '0', '5k', '有4年营销经理以上', '1', '湖北省-武汉市-洪山区');
INSERT INTO `companyrecruit` VALUES ('561b7e36ec52f2998e704ecef531c0da5d2931f6', '25ba7d18767019f4184b8baf8b7b718d9b4f5137', '产品经理', '<p>\n	<span style=\"line-height: 28px; widows: 1; font-family: SimSun; color: rgb(0,0,0); font-size: 16px\">首先，由主持人解说本次联谊会召开的目的和愿景，接着是双业集团董事长周亚刚先生简单介绍了双亚集团近年来的发展情况。例如，2012年由双亚集团研发的&ldquo;周大黑&rdquo;系列，引起社会各界强烈关注，&ldquo;周大黑&rdquo;系列的产品有周大黑黑米茶，周大黑谷物类巧克力，周大黑有机黑米，周大黑有机五彩米等等，其中主打产品周大黑黑米茶源自朱鹮之乡的必需营养素； 100%甄选原产地&bull;洋县有机黑米；富含活性花青素、植物源矿物微量元素及其他酚类物质等；茶色清凉如红珀，茶香淡雅如幽兰，茶味甘淳如玉霜。本产品申请了独一无二的特色专利。&ldquo;周大黑&rdquo;黑米茶不仅可以减肥美容还是有滋阴补肾、健脾暖肝、明目活血的作用，可以治疗贫血、头昏、视物不清、头发早白等多种病症。是一味不可多得的食疗药材。</span></p>\n', '17719714119', '400-666-5116', '123@qq.com', '闫宝宏', null, '2017-05-17 15:04:50', '陕西省汉中市洋县有机产业园区（洋县南环路东端）', '0', '5k', '一年', '1', '湖北省-武汉市-洪山区');
