INSERT INTO `companyrecruit` VALUES ('93dfee2104463d0d1a7d86e082a554ac849b596f', 'e3425c8be35ec5152a509673d9a29572a1c1216a', '矿山设备工程师', '<p style=\"text-align: center\">\n	<span style=\"font-size: 36px\"><span style=\"color: #ff0000\"><strong><em>矿山设备工程师</em></strong></span></span></p>\n<p style=\"text-align: center\">\n	因业务需要特对外招聘矿山设备工程师一名，工资可面议，具体职位要求如下：</p>\n<p>\n	&nbsp;</p>\n<p>\n	1、大专以上学历，具有7年以上矿山采选设备采购、安装、管理相关工作经验。</p>\n<p>\n	2、熟悉矿山各类采选设备，了解行业各类先进设备的更新情况，熟练掌握设备安装、验收、管理等工作。</p>\n<p>\n	3、能够对公司设备采购提出合理化建议，协助完成设备采购工作。</p>\n<p>\n	4、负责设备安装施工图的审核，工地现场考察，督促设计单位按要求对图纸进行修改和完善。</p>\n<p>\n	5、根据安装计划对施工方的安装进度进行督促、检查。根据相关标准对设备安装工程的施工质量进行控制，对施工单位的质量完成情况进行检查考核，并提出调整意见。</p>\n<p>\n	6、负责对设备安装材料、工程机械、安装队伍的质量检查与管理，检查设备安装过程中的问题并提出解决办法。</p>\n<p>\n	7、具有良好的计算机操作能力，熟悉设备安装制图软件，富有责任心、事业心及团队合作精神，有良好的沟通协调能力。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>\n	职位类别：&nbsp;&nbsp;&nbsp;&nbsp; 设备经理/主管&nbsp;</p>\n', '123456', '654321', '1255980609@qq.com', '廖雪岑', null, '2017-05-17 15:05:58', '洋县钒钛磁铁矿有限责任公司', '0', '一万', '有相关工作经验', '1', '湖北省-武汉市-洪山区');
