INSERT INTO `companyrecruit` VALUES ('d4bfecb7de6c2d48a3352d14639f0ee328e5186a', 'e3425c8be35ec5152a509673d9a29572a1c1216a', '土木/土建工程师', '<p style=\"text-align: center\">\n	<span style=\"font-size: 24px\"><span style=\"color: #8b4513\"><strong><em>土木/土建工程师</em></strong></span></span></p>\n<p>\n	因公司业务需要，特对外招聘土木工程师一名，工资根据情况面议，具体要求如下：</p>\n<p>\n	&nbsp;</p>\n<p>\n	1、大专以上学历，7年以上土建相关领域施工工作经验与现场管理经验。</p>\n<p>\n	2、熟悉国家及地方相关法规、政策，熟悉土建类施工图、施工管理和有关土建的施工规范及要求，掌握项目规划、建筑设计、施工、验收规范等基本建设程序。</p>\n<p>\n	3、负责土建图纸的审核，工地现场考察，督促设计单位按要求对图纸进行修改和完善；</p>\n<p>\n	4、负责协助招标工作，参加招标工程图纸答疑，草拟土建专业相关条款，审核土建专业报价是否符合相关规定及各项收费是否合理。</p>\n<p>\n	5、负责督促施工方在施工过程中的土建施工质量、进度和成本的控制，解决施工中出现的具体专业技术问题；</p>\n<p>\n	6、负责组织人员审查竣工资料和对单位工程及单项工程初验和竣工验收。</p>\n<p>\n	7、具有良好的计算机操作能力，熟悉工程制图软件，富有责任心、事业心及团队合作精神，有良好的沟通协调能力。</p>\n<p>\n	职位类别：&nbsp;&nbsp;&nbsp;&nbsp; 土建工程师&nbsp;</p>\n', '123456', '654321', '1255980609@qq.com', '廖雪岑', null, '2017-05-17 15:14:10', '洋县钒钛磁铁矿有限责任公司', '0', '5000~一万', '7年以上土建相关领域施工工作经验与现场管理经验', '1', '湖北省-武汉市-洪山区');
