INSERT INTO `companyrecruit` VALUES ('f33169938272bcbdc55c1991defbe982bfbca96e', 'e3e3e5d3ffa64bf51882fd19d5287b3da15c2844', '人事经理', '<div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;\">\n	第一、负责公司<a href=\"http://baike.baidu.com/item/%E4%BA%BA%E5%8A%9B%E8%B5%84%E6%BA%90%E8%A7%84%E5%88%92\" style=\"color: rgb(19, 110, 194); text-decoration: none;\" target=\"_blank\"><span style=\"color:#000000;\">人力资源规划</span></a>。根据公司销售业绩要求或生产规模要求，规划销售人员或生产人员数量，并确定员工素质条件，包括员工受教育水平、品德素质<span style=\"color:#000000;\">、</span><a href=\"http://baike.baidu.com/item/%E8%83%BD%E5%8A%9B%E7%B4%A0%E8%B4%A8\" style=\"color: rgb(19, 110, 194); text-decoration: none;\" target=\"_blank\"><span style=\"color:#000000;\">能力素质</span></a><span style=\"color:#000000;\">、工作经验等。</span></div>\n<div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;\">\n	<span style=\"color:#000000;\">第二、岗位说明书的制定及人事制度的制定和修改，包括&nbsp;</span><a href=\"http://baike.baidu.com/item/%E5%91%98%E5%B7%A5%E6%89%8B%E5%86%8C\" style=\"color: rgb(19, 110, 194); text-decoration: none;\" target=\"_blank\"><span style=\"color:#000000;\">员工手册</span></a><span style=\"color:#000000;\">、 公司招聘制度、</span><a href=\"http://baike.baidu.com/item/%E7%BB%A9%E6%95%88%E8%80%83%E6%A0%B8\" style=\"color: rgb(19, 110, 194); text-decoration: none;\" target=\"_blank\"><span style=\"color:#000000;\">绩效考核</span></a><span style=\"color:#000000;\">制度、培训制度、员工考勤制度等。</span></div>\n<div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;\">\n	第三、负责<span style=\"color:#000000;\">公司</span><a href=\"http://baike.baidu.com/item/%E5%91%98%E5%B7%A5%E6%8B%9B%E8%81%98\" style=\"color: rgb(19, 110, 194); text-decoration: none;\" target=\"_blank\"><span style=\"color:#000000;\">员工招聘</span></a><span style=\"color:#000000;\">面试</span>工作。按照公司人力资源年度规划，实施公司员工招聘。1）招聘准备：确定招聘人数，制定招聘文书，选择招聘方式；2）招聘实施：筛选简历确定面试人选；3）实施面试，确定试聘人选。</div>\n<div class=\"para\" label-module=\"para\" style=\"font-size: 14px; word-wrap: break-word; color: rgb(51, 51, 51); margin-bottom: 15px; text-indent: 2em; line-height: 24px; zoom: 1; font-family: arial, 宋体, sans-serif;\">\n	第四、负责公司员工合同管理。1）签订劳动合同；2）交纳劳动保险，加强与劳动保障部门的联系；3）劳动合同存档管理。</div>\n', '13209251876', '0916-8552298', '25074056812@qq.com', '陈凌霄', null, '2017-05-31 14:54:03', '陕西省洋县永辉农业发展有限公司', '5', '3.2k-4.0k', '1年-2年', '1', '陕西省-汉中市-洋县');
