INSERT INTO `ectrain` VALUES ('27f7eec0495d62bf8b881c1d3721f70c180c1d02', '电子商务培训', '0', '<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp;1、搜索引擎推广的优势、SEM/SEO策略、关键词的选择;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　2、定位营销策略，如何利用长尾关键词制胜技巧;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　3、网站内部结构优化、关键词布局及外链策略;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　4、SEO借力营销，让排名轻松靠前的技巧。</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　5、认识新闻及软文营销推广;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　6、提高博客营销影响力的几大技巧;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　7、企业微博营销如何有效利用粉丝产生价值;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　8、营销型网站建设的定位与布局介绍;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　9、细节决定成败，打造赢销型网站的技巧;</span><br style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\" />\n	<span style=\"color: rgb(51, 51, 51); font-family: arial, 宋体, sans-serif; font-size: 14px; line-height: 24px; text-indent: 28px;\">　　10、模拟网络营销实践操作，制定运营计划及网站营销推广计划;</span></p>\n', '120', '4', '40', '本科以上学历', '2017-05-22 18:47:09', '李先生', 'upload/pic/2017052218470469.jpg', null, '2017-05-01 10:41:00', '2017-06-14 10:41:00', '2017-06-30 10:41:00');
INSERT INTO `ectrain` VALUES ('a84da0bc121134fc319cf8e19252d32f08b4cc28', 'php程序员', '3', '<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第一课：PHP程序员培训开山篇</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二课：HTML基础（什么是标签、属性、元素、基本HTML的格式、代码注释以及编辑器、调试）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三课：常用的HTML标签之文本标签</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第四课：常用的HTML标签之列表和表格</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第五课：CSS基础（简介、语法以及引入）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第六课：CSS选择器（id、class、组合、嵌套、属性选择器）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第七课：CSS常用属性和值（宽高、字体大小、颜色、行高、对齐方式、背景、边框、链接、表格、列表）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第八课：CSS框模型(Box Model)（内边距、宽高、边框、外边距）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第九课：CSS布局（定位与浮动）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十课：HTML+CSS小结与综合练习（ps切片工具的使用、浏览器兼容性）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十一课：HTML5与CSS3介绍（HTML新增标签、CSS新增属性）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十二课：HTML技巧（图片拼接、代码压缩、减少嵌套、注意代码可维护性、SEO基本常识）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十三课：Javascript基础（JS简介、用法、调试）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十四课：JS变量、数据类型以及条件语句</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十五课：JS DOM与BOM</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十六课：JS函数、闭包以及作用域</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十七课：JS常用的函数（字符串、数组、日期、数学函数）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十八课：HTML + CSS + JS实战</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第十九课：Jquery讲解(选择器、函数、ajax、常用插件)</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十课：Nodejs讲解（安装、npm用法）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十一课：响应式布局以及bootstrap讲解</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十二课：正则表达式（简介、元字符、匹配规则）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十三课：WEB前端总结以及结课（对前期的内容总结以及展望）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十四课：PHP安装和WAMP使用</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十五课：PHP基础（简介、语法、数据类型）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十六课：PHP变量、数据类型以及条件语句</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十七课：PHP超级全局变量、魔术变量、命名空间</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十八课：PHP函数（字符串、数组、文件、数学、日期、HTTP、图像处理）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第二十九课：PHP会话cookie和session</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十课：MySQL简介和表设计</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十一课：MySQL操作（增、删、查、改、导入、导出）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十二课：MySQL常用函数（字符串、数学、时间、数组函数、日期函数、数据库函数）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十三课：MySQL高级之触发器以及事件</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十四课：PHP面向对象编程</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十五课：PHP+MySQL小结与综合练习(常用资源域名、服务器、短信网关)</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十六课：PHP常用框架ThinkPHP</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十七课：PHP CMS之WordPress和织梦</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十八课：网站的部署流程</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第三十九课：Linux简介以及CentOS下LAMP安装（面试时加分项）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第四十课：NoSQL相关介绍，包括Redis，Mongodb，Memcache（面试时加分项）</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第四十一课：PHP总结与商城练习</p>\n<p style=\"margin: 0px; padding: 0px; color: rgb(119, 119, 119); font-family: Arial, \'Microsoft YaHei\', 微软雅黑, STXihei, 华文细黑, sans-serif; font-size: 14px; line-height: 24px;\">\n	第四十二课：PHP程序员求职技巧（为人谦逊、自信大方)</p>\n', '150', '3', '30', '高中学历以上', '2017-05-22 18:50:32', '王先生', 'upload/pic/2017052218502681.jpg', null, '2017-05-01 10:49:00', '2017-05-24 10:49:00', '2017-05-31 10:49:00');
