INSERT INTO `platformrecruit` VALUES ('306b85e9dc53e810b9fe717e1cc30f6a6e3cdb48', '会计', '<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	岗位说明</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	1.负责相关产品规划、设计、实施、运营，撰写产品文档，协调资源实现产品，推进进度；</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	2.负责产品用户体验改善、深度挖掘用户需求，持续对产品优化改进；</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	3.负责创新产品行业观察、竞品分析；</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	4.与技术团队紧密配合，快速、高效推动产品设计、研发、运营及推广；</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	5.配合相关的市场、运营等部门，在产品层面对合作类项目予以支持。</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	岗位要求</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	1.3年以上互联网产品设计经验或游戏产品设计经验，具有移动互联网优秀产品经验优先；</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	2.了解最新的移动互联网动态信息，熟练掌握主流产品设计工具；</p>\n<p style=\"font-family: Verdana, Geneva, sans-serif; margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(68, 68, 68); line-height: 18px;\">\n	3.具备较强的沟通协调能力、分析能力、执行力、逻辑分析能力、数据分析力，善于倾听用户声音</p>\n', '18966557788', '7877565', '567847656@qq.com', '张三');
